import React, { Component } from "react";

class DarkModeToggle extends Component {
  render() {
    return (
      <button onClick={this.props.toggle}>
        {this.props.darkMode ? "Light Mode" : "Dark Mode"}
      </button>
    );
  }
}

export default DarkModeToggle;
